import App from '../../App';
import './Form.scss';

function Form() {
    return (
        <>
            <section className="comment-section">
                <h3 className="comment-section__total-comments">3 Comments</h3>
                <p className="comment-section__title">Join the Conversation</p>
                <textarea class="comment-section__text-box" name="commentInput"
                    placeholder="Write Comment Here" required></textarea>
                <button type="submit" className="comment-section__submit-button">COMMENT</button>
                <div className="comment-section__image-container">
                    <img className="comment-section__icon" src="./assets/images/Mohan-muruge.jpg"/>
                </div>
            </section>
        </>
    )
}

export default Form;
