import React from 'react';
import './App.scss';
import Form from './components/Form/Form';
import NavBar from './components/NavBar/NavBar';
import UploadForm from './components/UploadForm/UploadForm';
import VideoDescription from './components/VideoDescription/VideoDescription';
import VideoList from './components/VideoList/VideoList';
import VideoPlayer from './components/VideoPlayer/VideoPlayer';
import VideoDetails from './data/videoDetails';
import VideoListArray from './data/videos';

/* Page might load with a white border on the side. Reloading the page will fix the issue -- rendering issue */

class App extends React.Component {
  constructor() {
    super();
    this.updateState = this.updateState.bind(this);
    this.state = {
      index: 0,
      mainVideo: VideoDetails.find(video => video.id === videoDetails[0].id),
      videoList: VideoListArray.filter(video => video.id !== id)
    }
  }

  updateState(id) { 
    this.setState({
      mainVideo: VideoDetails.find(video => video.id === id),
      videoList: VideoListArray.filter(video => video.id != id)
    })
  }

  render() {
    return (
      <div className="App">
        <NavBar />
        {/* <UploadForm /> */}
        <VideoPlayer image={VideoDetails[this.state.mainVideo].image}/>
        <section className="desktop-container">
          <section className="desktop-container__video">
          <VideoDescription title={VideoDetails[this.state.mainVideo].title} uploader={VideoDetails[this.state.mainVideo].channel} 
          uploadDate={new Date(VideoDetails[this.state.mainVideo].timestamp).toLocaleDateString()} views={VideoDetails[this.state.mainVideo].views} 
          likes={VideoDetails[this.state.mainVideo].likes}  description={VideoDetails[this.state.mainVideo].description}/>
          <Form videoarray={VideoDetails[this.state.mainVideo].comments}/>
          </section>
          <section className="desktop-container__video-list">
            <VideoList videolist={VideoArray} update={this.updateState} data={this.state.index}/>
          </section>
        </section>
      </div>
    );
  }
}

export default App;


